mapper.select:
select *
from fill_registry_form
where user_id = #{userId}
  and form_id = #{formId}

mapper.delete:
delete
from fill_registry_form
where user_id = #{userId}
  and form_id = #{formId}
  and (select end_time from all_built_form where form_id = #{formId}) >= #{now}

mapper.update:
update fill_registry_form
set user_id=id
where user_id = #{userId}
  and form_id = #{formId}
